function a(e){let n=e.parentNode.querySelector("input.product-quantity");if(!n){const u=e.parentNode;return a(u)}return n}function o(){const e=document.querySelectorAll("button.plusBtn"),n=document.querySelectorAll("button.minusBtn");e.length&&e.forEach(u=>{u.addEventListener("click",r=>{const t=a(r.target);let l=Number(t.value);const i=Number(t.getAttribute("max"));l<i&&(t.value=l+1)})}),n.length&&n.forEach(u=>{u.addEventListener("click",r=>{const t=a(r.target);let l=Number(t.value);const i=Number(t.getAttribute("min"));l>i&&(t.value=l-1)})})}o();
